import { Model, DataTypes } from "sequelize";
import { sequelize } from "../database";
import { getHashPassword } from "../utils/password";

export interface userAttributes {
  Id: string;
  firstName: string;
  lastName: string;
  email: string;
  contactNumber: string;
  password: string;
  gender: string;
  role: string;
  enabled: boolean;
  deleted: boolean;
}
interface userInstance extends Model<userAttributes>, 
userAttributes {
  createdAt?: Date;
  updatedAt?: Date;
}

export const user = sequelize.define<userInstance>("user", {
  Id: {
    type: DataTypes.UUID,
    defaultValue: DataTypes.UUIDV4,
    primaryKey: true,
    allowNull: false, 
  },
  firstName: {
    type: DataTypes.STRING,
    allowNull: false,
  },
  lastName: {
    type: DataTypes.STRING,
  },
  email: {
    type: DataTypes.STRING,
    allowNull: false,
    unique: true,
  },
  contactNumber: {
    type: DataTypes.STRING,
    allowNull: false,
    unique: true,
  },
  password: {
    type: DataTypes.STRING,
    allowNull: false,
    set(value:string){
        this.setDataValue('password',getHashPassword(value))
    }
  },
  gender: {
    type: DataTypes.ENUM("male", "female", "others"),
  },
  role: {
    type: DataTypes.ENUM("admin", "doctor", "patient"),
    allowNull: false,
  },
  enabled: {
    type: DataTypes.BOOLEAN,
    defaultValue: true,
  },
  deleted: {
    type: DataTypes.BOOLEAN,
    defaultValue: false,
  },
});

import bcrypt from "bcryptjs"
const getHashPassword=(pswd:string):string=>{
   const salt = bcrypt.genSaltSync(10);
const hash = bcrypt.hashSync(pswd, salt)
return hash;
}


const matchedPassword=(pswd:string,storedPswd:string):boolean=>
{
  return  bcrypt.compareSync(pswd, storedPswd); 
}
export{getHashPassword,matchedPassword}

import { Model, DataTypes, Optional } from "sequelize";
import { sequelize } from "../database";
import { user } from "./users";


export interface doctorAttributes {
  Id: string;
  userId:string;
  languages?:Array<string>;
  bio?:string;
  image?:string;
  Qualification?:Array<string>;
  licenceNumber?:string;
  experience?:Array<string>
}
interface doctorCreationAttributes
  extends Optional<doctorAttributes, 'Id'> {}
interface doctorInstance extends Model<doctorAttributes,doctorCreationAttributes>, 
doctorAttributes {
  createdAt?: Date;
  updatedAt?: Date;
}

export const doctors = sequelize.define<doctorInstance>("doctor", {
  Id: {
    type: DataTypes.UUID,
    defaultValue: DataTypes.UUIDV4,
    primaryKey: true,
    allowNull: false, // Or DataTypes.UUIDV1
  },
  userId: {
    type: DataTypes.UUID,
    references: {
      model: user,
      key: "Id",
    },
  },
  languages: {
    type: DataTypes.ARRAY(DataTypes.JSON),
  },
  bio: {
    type: DataTypes.STRING,
  },
  image: {
    type: DataTypes.STRING,
  },
  Qualification: {
    type: DataTypes.ARRAY(DataTypes.JSON),
  },
  licenceNumber: {
    type: DataTypes.STRING,
  },
  experience: {
    type: DataTypes.ARRAY(DataTypes.JSON),
  },
});

doctors.belongsTo(user);

