export const getSpecialities=async(req:Request,res:Response)=>{
    
    
const name=req.query.name as string
let specialities:Array<Object>;

if(!name){
     specialities=await speciality.findAll()
}
else{
    specialities=await speciality.findAll({
    where:{
        name:{
            [Op.iRegexp]:name
        }
    }})
}

res.status(200).json({
